import React, {useEffect, useState} from "react";
import {Route, Routes} from 'react-router-dom';
import { fetchSneakers } from "./firebase/firebaseService";
import axios from "axios";

import Header from './components/Header';
import Drawer from "./components/Drawer";
import Home from "./pages/Home";
import Favorites from "./pages/Favorites";

function App() {
    const [ items, setItems] = useState([]);
    const [ cartItems, setCartItems ] = useState([]);
    const [ cartOpened, setCartOpened ] = useState(false);
    const [ searchValue, setSearchValue] = useState('');
    const [ favorites, setFavorites ] = useState([]);

    useEffect(() => {
            // axios.get('https://sneakers.free.beeceptor.com/items')
            //     .then(res => setItems(res.data))
            //     .catch(err => console.error("Error loading:", err))
        const loadSneakers = async () => {
            const sneakersData = await fetchSneakers();
            setItems(sneakersData);
        };
        loadSneakers();
        }, []);
    useEffect(() => {
        axios.get('https://sneakers.free.beeceptor.com/cart')
            .then(res => setCartItems(res.data))
            .catch(err => console.error("Error cart loading:", err))
    }, []);

    const onAddToCart = (obj) => {
        const existItem = cartItems.some(item => item.id === obj.id);
        if (!existItem) {
            axios.post('https://sneakers.free.beeceptor.com/cart', obj);
            setCartItems(prev => [...prev, obj]);
        }
    }
    const onRemoveItem = (id) => {
        axios.delete(`https://sneakers.free.beeceptor.com/cart/${id}`);
        setCartItems(prev => prev.filter(item => item.id !== id))
    }

    const onAddToFavorite = async (obj) => {
        try {
            if (favorites.find(favObj => favObj.id === obj.id)) {
                axios.delete(`https://sneakers.free.beeceptor.com/favorites/${obj.id}`)
                // setFavorites((prev) => prev.filter((item) => item.id !== obj.id));
            } else {
                const { data } = await axios.post("https://sneakers.free.beeceptor.com/favorites", obj);
                setFavorites((prev) => [...prev, data]);
            }
        } catch (err) {
            alert('Failed to add to favorites')
        }
    }

    const onChangeSearchInput = (event) =>{
        setSearchValue(event.target.value)
    }
    return (
        <div className="wrapper clear">
            {cartOpened && <Drawer items={cartItems} onClose={() => setCartOpened(false)} onRemove={onRemoveItem} />}
            <Header onClickCart={() => setCartOpened(true)} />

            <Routes>
                <Route path="/" element={
                    <Home
                        items={items}
                        searchValue={searchValue}
                        setSearchValue={setSearchValue}
                        onChangeSearchInput={onChangeSearchInput}
                        onAddToFavorite={onAddToFavorite}
                        onAddToCart={onAddToCart}
                    />
                } />
                <Route path="/favorites" element={
                    <Favorites
                        items={favorites}
                        onAddToFavorite={onAddToFavorite}
                    />
                } />
            </Routes>

            {/*<section className="content p-40">*/}
            {/*    <div className="d-flex justify-between align-center mb-40">*/}
            {/*        <h1>All SNEAKERS</h1>*/}
            {/*        <div className="searchBlock d-flex align-center">*/}
            {/*            <img width={14} height={14} src="/images/search.svg" alt="Search" />*/}
            {/*            <input onChange={onChangeSearchInput} value={searchValue} type="text" placeholder="Search..." />*/}
            {/*            {searchValue &&*/}
            {/*                <img*/}
            {/*                    onClick={()=> setSearchValue('')}*/}
            {/*                    className="clear cu-p"*/}
            {/*                    src="/images/btn-remove.svg"*/}
            {/*                    alt="Clear" />}*/}
            {/*        </div>*/}
            {/*    </div>*/}

            {/*    <div className="cards d-flex flex-wrap">*/}
            {/*        { items && items*/}
            {/*            .filter((item) => item.title.toLowerCase().includes(searchValue.toLowerCase()))*/}
            {/*            .map((item) => (*/}
            {/*            <Card*/}
            {/*                key={item.key}*/}
            {/*                id={item.key}*/}
            {/*                title={item.title}*/}
            {/*                price={item.price}*/}
            {/*                imgUrl={item.imgUrl}*/}
            {/*                onFavoritPlus={(obj) => onAddToFavorite(obj)}*/}
            {/*                onCartPlus={(obj) => onAddToCart(obj)}*/}
            {/*            />*/}
            {/*        ))}*/}
            {/*    </div>*/}
            {/*</section>*/}
        </div>
    );
}

export default App;
