import  { collection, getDocs, addDoc, getFirestore } from "firebase/firestore";
import { db } from "./firebase";

export const fetchSneakers  = async () => {
    const sneakersCollection = collection(db, "items");
    const snapshot = await getDocs(sneakersCollection);
    return snapshot.docs.map(doc => ({ id: doc.id, ...doc.data()}));
}

export const addToCart = async (sneaker) => {
    try {
        const cartCollection = collection(db, "cart");
        const docRef = await addDoc(cartCollection, sneaker);
        console.log("Sneaker added to cart")
    } catch (err) {
        console.error("Error adding Sneaker to cart:", err);
    }
};
