import React, {useRef, useState} from "react";

import styles from "./Card.module.scss";

export default function Card(props) {
    const [ isAdded, setIsAdded] = useState(false);
    const [ cartOpened, setCartOpened ] = useState(false);

    const onClickPlus = () => {
        setIsAdded(!isAdded);
    }

    return (
        <div className={styles.card}>
            <div className={styles.favorite} onClick={props.onFavoritPlus}>
                <img src="/images/favorite.svg" alt="Unliked"/>
            </div>
            <img width={133} height={112} src={props.imgUrl} alt="Sneakers"/>
            <h5>{props.title}</h5>
            <div className="d-flex justify-between align-center">
                <div className="d-flex flex-column ">
                    <span>Price: </span>
                    <b>{props.price} $</b>
                </div>
                {/*<button onClick={props.onCartPlus} className={styles.button}>*/}
                    <img className={styles.plus} onClick={onClickPlus} src={isAdded ? "/images/choose.svg" : "/images/plus.png" }alt="Plus"/>
                {/*</button>*/}
            </div>
        </div>
    )
}
