import React, {useEffect, useState} from "react";
import { v4 as uuidv4 } from "uuid";
import axios from "axios";

import Header from './components/Header';
import Card from "./components/Card";
import Drawer from "./components/Drawer";

function App() {
    const [ items, setItems] = useState([]);
    const [ cartItems, setCartItems ] = useState([]);
    const [ cartOpened, setCartOpened ] = useState(false);
    const [ searchValue, setSearchValue] = useState('');

    useEffect(() => {
        // fetch('https://672285cc2108960b9cc4bc75.mockapi.io/items')
        //     // .then((res) => { return res.json() })
        //     .then(res => res.json())
        //     .then(data => {
        //         const itemsWithIds = data.map(item => ({
        //             ...item,
        //             id: uuidv4()
        //         }));
        //         setItems(itemsWithIds)
        //     })
        //     // .then(json => setItems(json));
        //     .catch(err => console.error("Error loading:", err))

        axios.get('https://672285cc2108960b9cc4bc75.mockapi.io/items')
            .then(res => {
            const itemsWithIds = res.data.map(item => ({
                ...item,
                id: uuidv4()
            }));
            setItems(itemsWithIds)
        })
            .catch(err => console.error("Error loading:", err))
    }, []);
    const onAddToCart = (obj) => {
        // const existItem = cartItems.some(item => item.id === obj.id);
        // if (existItem) {
        //     return;
        // }
        //  else {
        //     setCartItems(prev => [...prev, obj]);
        // }
        setCartItems(prev => [...prev, obj]);
    }
    const onChangeSearchInput = (event) =>{
        setSearchValue(event.target.value)
    }
    return (
        <div className="wrapper clear">
            {cartOpened && <Drawer items={cartItems} onClose={() => setCartOpened(false)} />}
            <Header onClickCart={() => setCartOpened(true)} />

            <section className="content p-40">
                <div className="d-flex justify-between align-center mb-40">
                    <h1>All SNEAKERS</h1>
                    <div className="searchBlock d-flex align-center">
                        <img width={14} height={14} src="/images/search.svg" alt="Search" />
                        <input onChange={onChangeSearchInput} value={searchValue} type="text" placeholder="Search..." />
                        {searchValue &&
                            <img
                                onClick={()=> setSearchValue('')}
                                className="clear cu-p"
                                src="/images/btn-remove.svg"
                                alt="Clear" />}
                    </div>
                </div>

                <div className="cards d-flex flex-wrap">
                    { items
                        .filter((item) => item.title.toLowerCase().includes(searchValue.toLowerCase()))
                        .map((item) => (
                        <Card
                            key={item.title}
                            title={item.title}
                            price={item.price}
                            imgUrl={item.imgUrl}
                            onFavoritPlus={() => console.log('Added to favorite')}
                            onCartPlus={(obj) => onAddToCart(obj)}
                        />
                    ))}
                </div>
            </section>
        </div>
    );
}

export default App;
