import React, {useState} from "react";
import ContentLoader from "react-content-loader";

import styles from "./Card.module.scss";

export default function Card({
                                 id,
                                 // dbKey=null,
                                 imgUrl,
                                 title,
                                 price,
                                 onCartPlus,
                                 onFavorite,
                                 onRemoveCartPlus,
                                 isFavofite = false,
                                 isCart = false,
                                 isloading = true,

                                 favorited = false
}) {
    //const [isAdded, setIsAdded] = useState(false);
    //const [isFavorite, setIsFavorite] = useState(favorited);

    const onClickPlus = () => {
        if (isCart) {
            onRemoveCartPlus({id, imgUrl, title, price, isCart: !isCart})
        }
        onCartPlus({id, imgUrl, title, price, isCart: !isCart });
        //setIsAdded(!isAdded);
    }
    const onClickFavorite = () => {
        onFavorite({id, imgUrl, title, price,  isFavofite: !isFavofite});
       // setIsFavorite(!isFavorite);
    }
    return (
        <div className={styles.card}>
            {
                isloading ? (<ContentLoader
                        speed={2}
                        width={165}
                        height={250}
                        viewBox="0 0 150 265"
                        backgroundColor="#f3f3f3"
                        foregroundColor="#ecebeb"
                    >
                        <rect x="0" y="0" rx="10" ry="10" width="150" height="155"/>
                        <rect x="0" y="167" rx="5" ry="5" width="150" height="15"/>
                        <rect x="0" y="187" rx="5" ry="5" width="100" height="15"/>
                        <rect x="0" y="234" rx="5" ry="5" width="80" height="25"/>
                        <rect x="118" y="230" rx="10" ry="10" width="32" height="32"/>
                    </ContentLoader>)
                    :
                    (<>
                        <div className={styles.favorite} onClick={onClickFavorite}>
                            <img src={isFavofite ? "/images/favorite-like.svg" : "/images/favorite.svg"} alt="Unliked"/>
                        </div>
                        <img width='100%' height={135} src={imgUrl} alt="Sneakers" className={styles.imgCard} />
                        <h5>{title}</h5>
                        <div className="d-flex justify-between align-center">
                            <div className="d-flex flex-column ">
                                <span>Price: </span>
                                <b>{price} $</b>
                            </div>
                            <img className={styles.plus} onClick={onClickPlus}
                                 src={isCart ? "/images/choose.svg" : "/images/plus.png"} alt="Plus"/>
                        </div>
                    </>)
            }
        </div>
    // <div className={styles.card}>
    //     <div className={styles.favorite} onClick={onClickFavorite}>
    //         <img src={isFavofite ? "/images/favorite-like.svg" : "/images/favorite.svg"} alt="Unliked"/>
    //     </div>
    //     <img width={133} height={112} src={imgUrl} alt="Sneakers"/>
    //     <h5>{title}</h5>
    //     <div className="d-flex justify-between align-center">
    //         <div className="d-flex flex-column ">
    //             <span>Price: </span>
    //             <b>{price} $</b>
    //         </div>
    //         <img className={styles.plus} onClick={onClickPlus}
    //              src={isCart ? "/images/choose.svg" : "/images/plus.png"} alt="Plus"/>
    //         </div>

    )
}
